//list of retorts for console
var consoleArray = ["Yeah Right, lol!", "Try again", "nope", "seriously?", "Suddenly I feel better about life", "Try less", "I can do this all day", "facepalm", "oh boy", "whoops", "Are you even trying?", "You haven't quit yet?", "You do realize Chrome is recording this, right?", "It's ok, its just a game."];
//var to build array of words with indexes
var wordBank = [];
//var for word picked
var wordMatch = "";
//var to show the letter spaces at the beginning of game
var emptyBoard = [];
//var for correct guesses
var correctGuesses = [];
//var for incorrect guess
var incorrectGuesses = [];
//guess counter
var counter = 9;
//win counter
var wins = 0;
//loss counter
var losses = 0;
//boolean var when letter is correctly guessed
var correctPick = false;
//when a word is picked, it is split into an array
var wordLetters = [];


//vars for audio files
var wrongAns = document.createElement("audio");
var rightAns = document.createElement("audio");
var winnerSound = document.createElement("audio");
var losingSound = document.createElement("audio");
var wrongAgain = document.createElement("audio");
var rightAgain = document.createElement("audio");

wrongAns.setAttribute("src", "assets/audio/Record Scratch 2.ogg");
rightAns.setAttribute("src", "assets/audio/Toasty.ogg", "preload");
winnerSound.setAttribute("src", "assets/audio/Megalovania Intro.ogg");
losingSound.setAttribute("src", "assets/audio/Wrong Buzzer.ogg");
