//As far as I know, there is no pause or wait function in Javascript. Did some research and found this on SitePoint.com
function sleep(milliseconds) {
  var start = new Date().getTime();
  for (var i = 0; i < 1e7; i++) {
    // console.log(i);
    if ((new Date().getTime() - start) > milliseconds) {
      break;
    }
  }
}
//sets up game and is also used to reset the board.
function setupGame() {
  counter = 9;
  emptyBoard = [];
  correctGuesses = [];
  incorrectGuesses = [];
  document.getElementById("incorrectGuess").innerText = " ";
  //building array from word list
  wordBank = wordArray.split(" ");
  //picking random word from wordBank
  wordMatch = wordBank[Math.floor(Math.random() * wordBank.length)];
  //setting array to check guesses against
  wordLetters = wordMatch.split("");

  for (var i = 0; i < wordLetters.length; i++) {
    //building arrays to display on page
    emptyBoard[i] = " _";
    correctGuesses[i] = " _";
  }
  //update gameboard
  gameboard();
}

//setting up or resetting game
setupGame()


//waiting for a key pressed
document.onkeyup = function (event) {
  var guess = event.key;
  //if A-Z kepressed...
  if (event.keyCode >= 65 && event.keyCode <= 90) {
    //send key guess to guess checker function
    checkGuess(guess.toLowerCase());
  } else {
    //if wrong key pressed, play sound
    wrongAns.play();
  }
};

//setups gameboard to be displayed
function gameboard() {

  document.getElementById("gameBoard").innerHTML = emptyBoard.join(" ");
  document.getElementById("correctGuess").innerHTML = correctGuesses.join(" ");
  document.getElementById("incorrectGuess").innerHTML = incorrectGuesses.join(" ");
  document.getElementById("guessCounter").innerHTML = counter;
  document.getElementById("WinsCount").innerHTML = wins;
  document.getElementById("lossCount").innerHTML = losses;
}
//checks user guess against work picked
function checkGuess(letter) {

  //for the length of the word picked
  //letter is in array
  if (wordLetters.includes(letter)) {
    correctPick = true;

    if (correctGuesses.includes(letter)) {
      //if letter has already been correctly guessed right again answer sound played
      rightAgain.play();
    } else {
      //if letter is correctly guessed for the first time sound played
      rightAns.play();
    }
    // for loop to check letter guessed against ever letter in the word picked
    for (var i = 0; i < wordLetters.length; i++) {

      if (wordLetters[i] === letter) {
        //letter is added to the list of correct guesses
        correctGuesses[i] = letter;
        //letter is added to the list display under the current word
        emptyBoard[i] = letter;

        gameboard()

      }
      //checks to see is word guesses so far matches the entire word  
      if (correctGuesses.join("") == wordMatch) {
        //update wins counter..
        wins++;
        //updates board to display winning word
        gameboard();
        //plays winning sound
        winnerSound.play();
        //function to display window and pause game long enough for sound to finish playing
        sleep(5000);
        //correct word shown in window
        confirm("Your word was '" + wordMatch + "'. Would like to continue?");
        // resets game
        setupGame()
      }
    }

  } else {
    //
    console.log(consoleArray[Math.floor(Math.random() * consoleArray.length)]);
    //  console.log("running !correctPick, letter not found");
    //if incorrect guess not already in wrong letter list, then...
    if (!incorrectGuesses.includes(letter)) {
      wrongAns.play();
      //add wrong letter to list
      incorrectGuesses.push(letter.toUpperCase());
      //guess counter updated
      counter--;
    } else {
      wrongAgain.play();
    }

//i kept having an issue where the guess counter would get negative number
    if (counter < 0) {
      return
    } else if (counter === 0) {
      // console.log("end of game");
      losingSound.play();

      incorrectGuesses = [];
      losses++;
      counter = 9;
      gameboard();
      confirm("Your word was '" + wordMatch + "'. Would like to continue?");
      setupGame();
    }
  }
  gameboard();
}